define(["require","jquery","w3"],(require,$,w3)=>{let slide=w3.slideshow(".block",0);require("ajax")("GET","http://127.0.0.1:8080/chest/award").then(data=>{let index=0,awards=data.content;for(let award in awards){let awardImg,awardId=award.split("#")[0],indexId=awardId.split("award")[1],value=awards[award],awardBlock=$(".award-box li:eq("+index+")");indexId<10&&(awardImg=`<img src='./img/award/award0${indexId}.png' />`),indexId>10&&(awardImg=`<img src='./img/award/${awardId}.png' />`),awardBlock.append(awardImg),awardBlock.append(`<span class="awardSum">${value}</span>`),index++}}),$(".right").on("click",event=>{slide.next()}),$(".left").on("click",event=>{slide.previous()})});